import React from 'react';
import { Card, Button, CardImg, CardImgOverlay } from 'reactstrap';
import axios from 'axios';

export default class PhotoPage extends React.Component {

    state = {
        constraints: { audio: false, video: { width: 400, height: 300 } },
        filter: 'none',
        width:400,
        height:300,
        images:[],
        // user: JSON.parse(localStorage.me).
    }

    componentDidMount(){
        navigator.mediaDevices.getUserMedia(this.state.constraints)
            .then(stream => {
                const video = document.querySelector('video');            
                video.srcObject = stream;
                video.play();
            })
            .catch(function(err) {
                console.log(`Error: ${err}`);
            });
    }

    takePicture = () => {
        const { width,height,filter} = this.state
        // Create canvas
        const canvas = document.querySelector('canvas');
        const video = document.querySelector('video');  
        // const photos = document.getElementById('photos');          
        const context = canvas.getContext('2d');
        // set canvas props
        canvas.width = width;
        canvas.height = height;
        // Draw an image of the video on the canvas
        if (filter !== undefined) {
            // Set image filter
            context.filter = filter
            // Create image from the canvas
            context.drawImage(video, 0, 0, width, height);
            // create image url
            const imgUrl = canvas.toDataURL('image/png');
            // Set img src
            const image = {imgUrl:imgUrl}
            const album = [...this.state.images]
            // Add image to album
            album.push(image)
            this.setState({
              images: album
            })
        }
    }

    filterChange = (e)=> {
        e.preventDefault();
        const video = document.querySelector('video');  
        console.log(e.target.value)
        const photoFilter = e.target.value
        video.style.filter = photoFilter
        this.setState({
            filter : photoFilter,
        })
    }

    clearButton =()=>{
        const video = document.querySelector('video');  
        const photoFilter = document.getElementById('photo-filter');
        // Reset select list
        photoFilter.selectedIndex = 0;
        // Set video filter
        video.style.filter = 'none';
        this.setState({
            // Clear photos
            images:[],
            // Change filter back to none
            filter : video.style.filter
        })
    }

    handleUpload=(event)=> {
        event.preventDefault();

        axios({
            method: 'post',
            url: 'http://localhost:5000/api/v1/images/new',
            file: { 
                url: event.target.value,
                userid : this.state.user.id,
            }
        })
        .then(response => {
            // debugger
            console.log(response);
            localStorage.setItem('me', JSON.stringify(response.data));
            setTimeout(this.props.toggle,1000);;
            this.setState({
                isLogin : true,
                success : response.data.message,
            })
        })
        .catch(error => {
            // debugger
            this.setState({
                hasErrors : true,
                errors : error.response.data.message,
            })
        })
    }

    render() {
        const {images} = this.state
        return (
            <>
                <div className="top-container">
                    <video className="my-video">Stream not available...</video> 
                    <button id="photo-button" className="btn btn-dark" ref={(ref) =>{this.photoButton = ref;}} onClick={this.takePicture}>
                    Take Photo
                    </button>
                    <select id="photo-filter" className="select" onChange={this.filterChange}>
                        <option value="none">Normal</option>
                        <option value="grayscale(100%)">Grayscale</option>
                        <option value="sepia(100%)">Sepia</option>
                        <option value="invert(100%)">Invert</option>
                        <option value="hue-rotate(90deg)">Hue</option>
                        <option value="blur(5px)">Blur</option>
                        <option value="contrast(200%)">Contrast</option>
                        <option value="contrast(100%) brightness(150%)">Brightness</option>
                    </select>
                    <button id="clear-button" className="btn btn-light" onClick={this.clearButton}>Clear</button>
                    <canvas id="canvas"></canvas>
                </div>
                <div className="bottom-container">
                    {
                    images.map((images, index) =>(
                        <Card inverse key={index} style={{width:'50%'}}>
                            <CardImg src={images.imgUrl} alt={images.imgUrl} />
                            <CardImgOverlay className='d-flex align-items-end'>
                                <a href={images.imgUrl} download><Button color="primary">Download</Button></a>
                                <Button color="success" onClick={this.handleUpload} value={images.imgUrl}>Save</Button>
                            </CardImgOverlay>
                        </Card>
                    )
                    )
                    }
                </div>
            </>
        );
    }
}
