import React from 'react';
import MediaHandler from '../components/MediaHandler';


export default class VideoPage extends React.Component {
    constructor() { 
        super();
        this.state = {
            hasMedia: false,
            otherUserId: null
        }
        this.user = JSON.parse(localStorage.me).user;

        this.mediaHandler = new MediaHandler();

    }

    componentWillMount(){

        this.mediaHandler.getPermissions()
        .then(stream => {
            this.setState({hasMedia:true});
            this.user.stream = stream;
            try {
                this.myVideo.srcObject = stream;

            } catch(e){
                this.myVideo.src = URL.createObjectURL(stream);
            }
            this.myVideo.play();
        })

    }



    render() {
        return (
            <>
                <div className="top-container">
                    <div className="video-container">
                        <video className="my-video" ref={(ref) =>{this.myVideo = ref;}}>Stream not available...</video> 
                    </div>
                    <button id="photo-button" className="btn btn-dark">
                    Take Photo
                    </button>
                    <select id="photo-filter" className="select">
                    <option value="none">Normal</option>
                    <option value="grayscale(100%)">Grayscale</option>
                    <option value="sepia(100%)">Sepia</option>
                    <option value="invert(100%)">Invert</option>
                    <option value="hue-rotate(90deg)">Hue</option>
                    <option value="blur(10px)">Blur</option>
                    <option value="contrast(200%)">Contrast</option>
                    </select>
                    <button id="clear-button" className="btn btn-light">Clear</button>
                    <canvas id="canvas"></canvas>
                </div>
                <div className="bottom-container">
                    <div id="photos"></div>
                </div>
            </>
        );
    }
}
