import React from 'react';
import background from '../components/background-calm-clouds-747964.jpg';

export default class HomePage extends React.Component {
    state = {
        filter : 'none'
    }
    filterChange = (e)=> {
        e.preventDefault();
        const video = document.querySelector('video');  
        console.log(e.target.value)
        const photoFilter = e.target.value
        video.style.filter = photoFilter
        this.setState({
            filter : photoFilter,
        })
    }

    render() {
        // const {users} = this.props;
        return (
            <>
                <div style={{position:'absolute',top:'0',left:'0',width:'100%',height:'100vh',overflow:'hidden',zIndex:'-2'}}>
                    <img className="m-0" src={background} alt="background" />
                </div>
                <canvas id="canvas"></canvas>
                <div style={{background:'black',opacity:'0.1',position:'absolute',top:'0',left:'0',width:'100%',height:'100vh',overflow:'hidden',zIndex:'-1'}} >
                   <select id="photo-filter" className="select" style={{top:'50%',left:'50%',position:'absolute'}} onChange={this.filterChange}>
                        <option value="none">Normal</option>
                        <option value="grayscale(100%)">Grayscale</option>
                        <option value="sepia(100%)">Sepia</option>
                        <option value="invert(100%)">Invert</option>
                        <option value="hue-rotate(90deg)">Hue</option>
                        <option value="blur(5px)">Blur</option>
                        <option value="contrast(200%)">Contrast</option>
                        <option value="contrast(100%) brightness(150%)">Brightness</option>
                    </select>
                </div>
            </>
            // <div className='mx-5'>
            //     {
            //     users.map((users, index) =>(
            //         <div key={index} className='bg-light shadow-sm mb-5 p-3 rounded row'>
            //             <div className="col-3">
            //                 {/* <Link to={`/users/${users.id}`} ></Link> */}
            //                 <h4 style={{fontFamily:'monospace'}} className="text-capitalize text-dark">{users.username}</h4>
            //                 <p className="card-text">{users.email}</p>
            //             </div>
            //             <div className="col-9">
            //             </div>               
            //         </div>
            //     )
            //     )
            //     }
            // </div>

        )
    }
  }
