import React from 'react';
import MediaHandler from '../components/MediaHandler';


export default class VideoPage extends React.Component {
    constructor() { 
        super();
        this.state = {
            // constraints: { audio: false, video: { width: 400, height: 300 } },
            filter: null,
            width:400,
            height:300
        }
        this.user = JSON.parse(localStorage.me).user;

        this.mediaHandler = new MediaHandler();

    }

    componentDidMount(){
        this.mediaHandler.getPermissions()
        .then(stream => {
            const video = document.querySelector('video');            
            video.srcObject = stream;
            video.play();
        })
        .catch(function(err) {
            console.log(`Error: ${err}`);
        });
    }

    takePicture = () => {
        const { width,height} = this.state
        // Create canvas
        const canvas = document.querySelector('canvas');
        const video = document.querySelector('video');  
        const photos = document.getElementById('photos');          
        const context = canvas.getContext('2d');
        // set canvas props
        canvas.width = width;
        canvas.height = height;
        // Draw an image of the video on the canvas
        context.drawImage(video, 0, 0, width, height);
        // Create image from the canvas
        const imgUrl = canvas.toDataURL('image/png');
        // Create img element
        const img = document.createElement('img');

        // Set img src
        img.setAttribute('src', imgUrl);

        // Set image filter
        // img.style.filter = filter;

        // Add image to photos
        photos.appendChild(img);
    }
    filterChange = (e)=> {
        // const filter = document.getElementById('photo-filter');
        const video = document.querySelector('video');  
        console.log(e.target.value)
        this.setState({
            filter : e.target.value,
        })
        video.style.filter = this.state.filter
    }


    render() {
        return (
            <>
                <div className="top-container">
                    <video className="my-video">Stream not available...</video> 
                    <button id="photo-button" className="btn btn-dark" ref={(ref) =>{this.photoButton = ref;}} onClick={this.takePicture}>
                    Take Photo
                    </button>
                    <select id="photo-filter" className="select" onChange={this.filterChange}>
                    <option value="none">Normal</option>
                    <option value="grayscale(100%)">Grayscale</option>
                    <option value="sepia(100%)">Sepia</option>
                    <option value="invert(100%)">Invert</option>
                    <option value="hue-rotate(90deg)">Hue</option>
                    <option value="blur(10px)">Blur</option>
                    <option value="contrast(200%)">Contrast</option>
                    </select>
                    <button id="clear-button" className="btn btn-light" ref={(ref) =>{this.clearButton = ref;}}>Clear</button>
                    <canvas id="canvas"></canvas>
                </div>
                <div className="bottom-container">
                    <div id="photos"></div>
                </div>
            </>
        );
    }
}
