// import React from 'react';

export default class MediaHandler {
    getPermissions(){
        return new Promise((resolve,reject)=> {
            navigator.mediaDevices.getUserMedia({video:true})
                .then((stream) =>{
                    resolve(stream);
                })
                .catch(err => {
                    throw new Error(`Error: ${err}`);
                })
        });
    }
}
